import axios from 'axios';

const apiUrl = "http://localhost:5221/items"
axios.defaults.baseURL= "http://localhost:5221/items"

export default {
  getTasks: async (item) => {
    // const result = await axios.get(`${apiUrl}`)    
    // return result.data;
    const result = await(`${apiUrl}`)    
    return result.data;
  },

  addTask: async(name)=>{
    var item={Id:0,Name:name,IsComplete:false}
    await axios.post(`${apiUrl}`,item)
    console.log('addTask', item)
    //TODO
    return {};
  },

  setCompleted: async(id,name, isComplete)=>{
    await axios.put(`${apiUrl}/${id}`,{Name:name,IsComplete:isComplete})
    console.log('setCompleted', {id, isComplete})
    //TODO
    return {};
  },

  deleteTask:async(id)=>{
    await axios.delete(`${apiUrl}/${id}`)
    console.log('deleteTask')
  }
};
